// src/app/components/travel-packages/travel-packages.component.ts

import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-travel-package',
  templateUrl: './travel-package.component.html',
  styleUrls: ['./travel-package.component.css']
})
export class TravelPackageComponent implements OnInit {
  travelPackages: any[] = [];

  constructor(private http: HttpClient, private router: Router) {}

  ngOnInit(): void {
    this.http.get('http://localhost:8000/api/travel-packages', { withCredentials: true }).subscribe(
      (packages: any) => {
        this.travelPackages = packages;
      },
      (error) => {
        console.error('Error fetching travel packages:', error);
      }
    );
  }

  // Method to navigate to the package profile with the specific package ID
  goToPackageProfile(packageId: any): void {
    this.router.navigate(['/package-profile', packageId]);
  }
}
<!-- src/app/components/travel-packages/travel-packages.component.html -->

<div class="photo-container">
  <h2>Explore the World</h2>
  <p>Discover amazing destinations with our travel packages.</p>
</div>

<h1>Travel Packages</h1>

<div *ngIf="travelPackages.length > 0; else noPackages" class="travel-packages-container">
  <div *ngFor="let package of travelPackages" class="package-item">
      <h2>{{ package.name }}</h2>
      <p>
          <strong>Description:</strong> {{ package.description }}<br>
          <strong>Destination ID:</strong> {{ package.destination_id }}<br>
          <strong>Price:</strong> ${{ package.price }}
      </p>
      <!-- Add more details as needed -->
      <button class="view-details-button" (click)="goToPackageProfile(package.id)">View Details</button>
  </div>
</div>

<ng-template #noPackages>
  <p>No travel packages available.</p>
</ng-template>
