import { Component } from '@angular/core';
import { Emitters } from '../emiters/emitters';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-temp',
  templateUrl: './temp.component.html',
  styleUrls: ['./temp.component.css']
})
export class TempComponent {

  message:any="";
  auth=false;
  admin=false
  price:any;

  constructor(private http: HttpClient, private router: Router) {
  }

  ngOnInit(): void {
    this.http.get('http://localhost:8000/api/user', {withCredentials: true}).subscribe(
      (res: any) => {
        this.message = `Hi ${res.name}`;
        console.log(res)
        if (res.admin_access==1){
          Emitters.adminEmitter.emit(true);
          this.message=this.message+ " You're an admin";
        }
        Emitters.authEmitter.emit(true);
      }
    );
    Emitters.authEmitter.subscribe(
      (data: any) => {
        this.auth= data;
        console.log("This is working1");
      }
    );
    Emitters.adminEmitter.subscribe(
      (data: any) => {
        this.admin= data;
        console.log("This is working2");
      }
    );
  


    // Emitters.dataEmitter.subscribe(         FOR RECEIVING DATA FROM ANOTHER COMPONENT
    //   (data: any) => {
    //     this.user = data;
    //     console.log("This is working");
    //     console.log(this.user)
    //   }
    // );
  }

  logout(): void {
    this.http.post('http://localhost:8000/api/logout', {}, {withCredentials: true})
      .subscribe((succ: any) => {
        this.auth = false
        console.log(succ)
        this.router.navigate(['/login'])
      });
  }

  payment(){
    
    
   
    let bodyData = {
      "price" : this.price
    };


    this.http.post("http://localhost:8000/api/paypal/payment",bodyData,{withCredentials: true}).subscribe((resultData: any)=>
    {  
      window.location.href=resultData['link']
    });
    console.log("This runs")
    
  
}

}
